import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom";

const Dashboard = () => {
  let classname = {
    selected: "item-selected",
    notSelected: "item-notSelected",
  };

  const [user, setUser] = useState([]);

  const [navigation, setNavigation] = useState([
    {
      name: "Dashboard",
      href: "#",
      current: true,
    },
    {
      name: "Add Expense Folder",
      href: "#",
      current: false,
    },
    {
      name: "Profile",
      href: "#",
      current: false,
    },
    {
      name: "Settings",
      href: "#",
      current: false,
    },
    {
      name: "Sign Out",
      href: "#",
      current: false,
    },
  ]);

  const location = useLocation();

  const userId = location.split("/")[2]; // this will get the id of the user
  const handleClick = (clickedItem) => {
    // Update the state to mark the clicked item as current
    setNavigation((prevNavigation) =>
      prevNavigation.map((item) => ({
        ...item,
        current: item.name === clickedItem.name,
      }))
    );

    console.log(clickedItem);
  };

  useEffect(() => {
    const getData = async () => {
      try {
        const userData = await axios.get(
          "http://localhost:8800/api/v1/user" + userId,
          user
        );
        setUser(userData);
      } catch (err) {
        console.log(err);
      }
      console.log(user);
    };

    getData();
  }, []);

  return (
    <div className="main">
      <nav className="navigation-bar-container">
        <div className="navigation-bar">
          <div className="navigation-bar-items-container">
            <div className="navigation-bar-items">
              <div className="navigation-items">
                <div className="items">
                  {navigation.map((item) => (
                    <a
                      key={item.name}
                      href={item.href}
                      className={
                        item.current
                          ? classname.selected
                          : classname.notSelected
                      }
                      aria-current={item.current ? "page" : undefined}
                      onClick={() => handleClick(item)}
                    >
                      {item.name}
                    </a>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      </nav>

      <div className="dashboard-container">
        <div className="dashboard-title-container">
          <h1 className="dashboard-title">Dashboard</h1>
        </div>
      </div>

      <div className="cards"></div>
    </div>
  );
};

export default Dashboard;
