import React from "react";
import { useNavigate } from "react-router-dom";
import "../styling/sign_out_modal.css";

const SignOutModal = ({ isOpen, closeModal, navLinks }) => {
  const navigation = useNavigate();

  const handleOutsideModalClick = (e) => {
    e.preventDefault();
    closeModal(false);

    if (navLinks) {
      if (navLinks[0].href) {
        navLinks[0].current = true;
        navLinks[navLinks.length - 1].current = false;
      }
    } else {
      navLinks[navLinks.length - 1].current = false;
    }
  };

  const handleContentClick = (e) => {
    e.stopPropagation();
  };

  const handleButtonClick = (e) => {
    e.preventDefault();

    if (e.target.name === "yes") {
      navigation("/");
    } else {
      closeModal(false);
    }
  };

  return (
    <div
      className={`modal ${isOpen ? "open" : ""}`}
      onClick={handleOutsideModalClick}
    >
      <div className="modal-overlay">
        <div className="modal-content" onClick={handleContentClick}>
          <div className="modal-title-container">
            <h1 className="modal-title">Sign Out?</h1>
          </div>

          <div className="sign-out-modal-button-container">
            <button
              className="sign-out-modal-button"
              name="yes"
              onClick={handleButtonClick}
            >
              Yes
            </button>

            <button
              className="sign-out-modal-button"
              name="no"
              onClick={handleButtonClick}
            >
              No
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignOutModal;
