import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import passwordValidation from "../validation/ChangePasswordValidation";

const ForgotPassword = () => {
  const [values, setValues] = useState({
    email: "",
    password: "",
  });

  const [errors, setErrors] = useState({});

  const navigate = useNavigate();

  const handleChange = (e) => {
    setValues((prev) => ({ ...prev, [e.target.name]: e.target.value }));
    console.log(values);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    let errorMessages = passwordValidation(values);
    console.log(errorMessages);
    setErrors((prev) => ({ ...prev, errorMessages }));
    try {
      await axios
        .post("http://localhost:8800/api/v1/forgot_password", values)
        .then((response) => {
          console.log(response);
          console.log(errors);
          if (
            errorMessages.badLength ||
            errorMessages.noNumericalChar ||
            errorMessages.noSpecialChar ||
            errorMessages.noUppercaseChar
          ) {
            setErrors((prev) => ({ ...prev, errorMessages }));
          } else {
            navigate("/");
          }
        });
    } catch (err) {
      console.log(err);
    }
  };

  //   useEffect(() => {
  //     const submitData = async (e) => {
  //       if (Object.values(errors).every((error) => error === "")) {
  //         await axios.post(
  //           "http://localhost:8800/api/v1/forgot_password",
  //           values
  //         );
  //         navigate("/");
  //       }
  //     };

  //     submitData();
  //   }, [values, errors, navigate]);

  return (
    <div className="forgot-password">
      <div className="title-container">
        <h1 className="title">Forgot Password?</h1>
      </div>

      <div className="form-container">
        <form className="form" onSubmit={handleSubmit}>
          <div className="container">
            <label>Please Enter Your Email</label>
            <div className="input-container">
              <input type="text" name="email" onChange={handleChange} />
            </div>
          </div>

          <div className="container">
            <label>Please Enter Your New Password</label>
            <div className="input-container">
              <input type="text" name="password" onChange={handleChange} />
              {errors.noUppercaseChar && (
                <span className="error-text">{errors.noUppercaseChar}</span>
              )}
              {errors.noNumericalChar && (
                <span className="error-text">{errors.noNumericalChar}</span>
              )}
              {errors.noSpecialChar && (
                <span className="error-text">{errors.noSpecialChar}</span>
              )}
              {errors.badLength && (
                <span className="error-text">{errors.badLength}</span>
              )}
            </div>
          </div>

          <div className="formButton-container">
            <button className="formButton" type="submit">
              Finish
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ForgotPassword;
