import React from "react";
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import signUpValidation from "../validation/SignUpValidation";

const SignUp = () => {
  const [user, setUser] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
  });

  const navigate = useNavigate();
  let errorMessages = {};

  const handleChange = (e) => {
    setUser((prev) => ({ ...prev, [e.target.name]: e.target.value }));
    // console.log(user);
  };

  const handleSubmit = async (e) => {
    e.preventDefault(); // stops page from refreshing. If page refreshes, the inputted data is lost
    try {
      // console.log(user);
      errorMessages = signUpValidation(user);
      console.log(errorMessages);
      await axios.post("http://localhost:8800/api/v1/register", user);
      navigate("/");
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="sign-up">
      <div className="title-container">
        <h1 className="title">Sign Up For Express Tracker</h1>
      </div>

      <div className="form-container">
        <form className="form" onSubmit={handleSubmit}>
          <div>
            <label>First Name</label>
            <div className="fname">
              <input type="text" name="firstname" onChange={handleChange} />
            </div>
          </div>

          <div>
            <label>Last Name</label>
            <div className="lname">
              <input type="text" name="lastname" onChange={handleChange} />
            </div>
          </div>

          <div>
            <label>Email address</label>
            <div className="input-container">
              <input type="text" name="email" onChange={handleChange} />
              {errorMessages.isEmptyEmail && (
                <span className="error-text">Email cannot be empty</span>
              )}
            </div>
          </div>

          <div>
            <label>Password</label>
            <div className="input-container">
              <input type="password" name="password" onChange={handleChange} />
            </div>
          </div>

          <div>
            <button className="formButton" type="submit">
              Sign Up
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default SignUp;
